package com.myc.sample.basics;

public class DisplayHello {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		float f = 3.14f;
		System.out.println("Hello World..");
		System.out.println();

		if(f == 3.14f){
			System.out.println("true");
		}else {
			System.out.println("false");
		}
	}

}
